WorkflowProcessEvent.eventDescription:
function afterProcessCreate(processId){
    	var today = new Date();
		var yyyy = today.getFullYear();
        var year = parseInt(yyyy) + 1;
        // Seta o ano de vigência seguindo as regras de negócio(ano de abertura do processo + 1)
        hAPI.setCardValue("anoVigencia", year);
        // Trava a abertura de novas solicitações para o ano. 
        hAPI.setCardValue("hasOrcamentoThisYear", "Sim");
        // Grava o número de processos no registro
        hAPI.setCardValue("numProcess", processId);
        // Seta o estado do processo como vazio
        hAPI.setCardValue("processState", "");
}

function beforeCancelProcess(colleagueId, processId){
    // Seta o estado do processo como 'CANCELADO' no registro, antes de cancelar o processo.
    hAPI.setCardValue("processState", "CANCELADO");
}

function beforeTaskSave(colleagueId,nextSequenceId,userList){
    var atividade = getValue('WKNumState');
	
    if ( atividade == 0 || atividade == 4 ) {
        var year = hAPI.getCardValue("anoVigencia");
        checkOrcamentoThisYear(year);
    }
    
    if( getValue("WKCompletTask") == "true" ){
		atualizaHistorico("obsHistorico");
        log.info("HISTÓRICO ATUALIZADO");
	} else {
        log.info("HISTÓRICO NÃO ATUALIZADO");
    }
    
    function atualizaHistorico(name) {
        if( name == "" ){
            return;
        }
        var mensagem = hAPI.getCardValue(name);

        if(mensagem == null || mensagem == ""){
            return;
        }

        var ultimaAtualizacao = hAPI.getCardValue("ultimaAtualizacao") == "" ? " " : hAPI.getCardValue("ultimaAtualizacao");
        var historico = hAPI.getCardValue("historico")== "" ? " " : hAPI.getCardValue("historico");
        
        var usuarioLogado = "";
        try {
            usuarioLogado = usuario();
        } catch(err){
            usuarioLogado = "Erro ao buscar usuário";
        }

        var htmlHistoricoNovo = dataHoraAtual()+" - "+usuarioLogado+"  \r\n" + mensagem +"\r\n \r\n";
        
        hAPI.setCardValue("ultimaAtualizacao", htmlHistoricoNovo );
                    
        hAPI.setCardValue("historico", ultimaAtualizacao + historico );
        hAPI.setCardValue(name, "" );
    }


    function dataHoraAtual() {
        var dt = new Date();
        var txtData = (dt.getDate() < 10 ? "0" + dt.getDate() : dt.getDate()) + "/" + ((dt.getMonth() + 1) < 10 ? "0" + (dt.getMonth() + 1) : (dt.getMonth() + 1)) + "/" + dt.getFullYear() + " - " + (dt.getHours() < 10 ? "0" + dt.getHours() : dt.getHours()) + ":" + (dt.getMinutes() < 10 ? "0" + dt.getMinutes() : dt.getMinutes());
        return txtData;
    }


    function usuario(){
        var c1 = DatasetFactory.createConstraint("colleaguePK.colleagueId",getValue("WKUser"),getValue("WKUser"),ConstraintType.MUST);
        var dsUser = DatasetFactory.getDataset("colleague",["colleagueName"],[c1],null);
        return dsUser.getValue(0,"colleagueName");
    }
    /**
     * @param {int} year - ano vigente da solicitação
     * @description - Caso já exista um orçamento para o ano informado, 
     * é exibido um mensagem ao usuário.
     */
    function checkOrcamentoThisYear(year) {
		var c1 = DatasetFactory.createConstraint("metadata#active", true, true,ConstraintType.MUST);
		var c2 = DatasetFactory.createConstraint("anoVigencia", year,year,ConstraintType.SHOULD); 
		var c3 = DatasetFactory.createConstraint("processState", "CANCELADO", "CANCELADO",ConstraintType.MUST_NOT);
		var orcamentos = DatasetFactory.getDataset('cadastro_orcamentos',null,[c1,c2,c3],null);
		if ( orcamentos.values.length > 0 ) {
			throw ("Já existe um planejamento de orçamentos anuais para este ano.");
		}
	}

}

function startconditional34() {
    var today = new Date();
    var yyyy = today.getFullYear();
    var year = parseInt(yyyy) + 1;
    log.info("GET COUNT DEPARTAMENTOS");
    var countDepartamentos = getCountDepartamentos();
    log.info("GET COUNT PROCESSOS OK NO ANO VIGENTE");
    var countProcessOk = getCountDepartamentoPlanejamentoThisYear(year);
    log.info("COUNT DEPARTAMENTOS: " + countDepartamentos);
    log.info("COUNT PROCESSOS OK: " + countProcessOk);

    var hasOrcamento = hasOrcamentoThisYear(year);
    if (countDepartamentos == countProcessOk && hasOrcamento != "Sim") {
        log.info("COUNT PROCESSOS QUE AGUARDAM ORÇAMENTOS == COUNT DEPARTAMENTOS");
        return true;
    } else {
        if (hasOrcamento == "Sim") {
            log.info("EXISTE ORÇAMENTO PRO ANO DE: " + year);
        } else {
            log.info("COUNT PROCESSOS QUE AGUARDAM ORÇAMENTOS !== COUNT DEPARTAMENTOS");
        }
        return false;
    }
}
/**
 * @param {int} ano - Ano que será objeto da pesquisa.
 * @returns {int} - Quantidade de departamentos com planejamento com status OK no ano especificado
 * @description Retorna quantidade de departamentos que possuem um planejamento de treinamentos
 * em um ano específico.
 */
function getCountDepartamentoPlanejamentoThisYear(ano) {
    var c1 = DatasetFactory.createConstraint("metadata#active", true, true, ConstraintType.MUST);
    var c2 = DatasetFactory.createConstraint("anoVigencia", ano.toString(), ano.toString(), ConstraintType.MUST);
    var c3 = DatasetFactory.createConstraint("processState", "OK", "OK", ConstraintType.MUST);
    var dataset = DatasetFactory.getDataset('propor_treinamentos_anuais', null, [c1, c2, c3], ['documentid']);
    if (dataset.rowsCount > 0) {
        return dataset.rowsCount;
    }
    return 0;
}
/**
 * @returns {int} - Quantidade de departamentos no dataset.
 * @description - Retorna a quantidade de departamentos cadastrados no dataset.
 */
function getCountDepartamentos() {
    var c1 = DatasetFactory.createConstraint("metadata#active", true, true, ConstraintType.MUST);
    var data = DatasetFactory.getDataset("cadastro_areas_orcamento", null, [c1], ["documentid"]);
    if (data.rowsCount > 0) {
        return data.rowsCount;
    } else {
        return 0;
    }

}
/**
 * @param {int} year - Ano que será objeto da pesquisa.
 * @returns {string} - 'Sim' ou 'Não'
 * @description - Retorna 'Sim' caso já tenha um planejamento de orçamentos para o ano informado 
 * ou, do contrário, retorna 'Não'.
 */
function hasOrcamentoThisYear(year) {
    var c1 = DatasetFactory.createConstraint("metadata#active", true, true, ConstraintType.MUST);
    var c2 = DatasetFactory.createConstraint("anoVigencia", year, year, ConstraintType.MUST);
    var c3 = DatasetFactory.createConstraint("hasOrcamentoThisYear", "Sim", "Sim", ConstraintType.MUST);
    var data = DatasetFactory.getDataset("cadastro_orcamentos", null, [c1, c2, c3], ["documentid"]);
    if (data.rowsCount > 0) {
        return "Sim";
    }
    return "Não";
}

function servicetask19(attempt, message) {
	try {
		   log.warn('%%%%%% INICIANDO ECMCardService');
		   var servico = ServiceManager.getServiceInstance("ECMCardService");
		   log.warn('%%%%%% servico: ' + servico);

		   var locator = servico.instantiate("com.totvs.technology.ecm.dm.ws.ECMCardServiceService");
		   log.warn('%%%%%% locator: ' + locator);

		   var portServico = locator.getCardServicePort();
		   log.warn('%%%%%% portServico: ' + portServico);

		   var user = hAPI.getAdvancedProperty("loginUserWS");
		   log.warn('%%%%%% user: ' + user);

		   var password = hAPI.getAdvancedProperty("passwdUserWS");
		   log.warn('%%%%%% password: ' + password);

		   var empresa = parseInt(getValue("WKCompany"));
		   log.warn('%%%%%% empresa: ' + empresa + ' TypeOf: ' + typeof empresa);

		   /** Form Data */
		   // YEAR
		   var today = new Date();
		   var yyyy = today.getFullYear();
		   var year = parseInt(yyyy) + 1;
		   log.warn('%%%%%% ANO VIGÊNCIA: ' + year);
		   //get DATASET
		   var datasetPlanejamentos = getAllPlanejamentos();

		   if ( datasetPlanejamentos.rowsCount > 0 ) {
			   hAPI.setCardValue("anoVigencia", year.toString());
			   for ( var i = 0; datasetPlanejamentos.rowsCount > i ; i++ ) {
				   // obter número da solicitação
				   var numSolic = datasetPlanejamentos.getValue(i, "workflowProcessPK.processInstanceId");	
				   // obtendo um objeto com os valores de cada solicitação de planejamento
				   var solicObj = hAPI.getCardData(numSolic);
				   var anoVigenciaObj = solicObj.get("anoVigencia");
				   if ( anoVigenciaObj == year ) {
					   var situacao = solicObj.get("processState");
					   if ( situacao != "EXECUCAO1" && situacao != "EXECUCAO_OK" && situacao != "CANCELADO" && situacao != "FINALIZADO") {
						   var departamento = solicObj.get("departamento");
						   var estimativa = solicObj.get("totalEstimativa");
						   var documentId = parseInt( solicObj.get("documentid") );
						   //Criando uma row na tabela pai-filho de orcamentos
						   var childData = new java.util.HashMap();
						   childData.put("numeroSolicitacaoTbOrcamentos", numSolic);
						   childData.put("numeroDocIdTbOrcamentos", parseInt(documentId));
						   childData.put("departamentoTbOrcamentos", departamento);
						   childData.put("estimativaTbOrcamentos", estimativa);
						   childData.put("situacao", situacao);
						   log.warn("%%%%% childData: "+childData);
						   hAPI.addCardChild("orcamentos", childData);

						   //  update state planejamento
						   var cardDocArray = servico.instantiate("com.totvs.technology.ecm.dm.ws.CardFieldDtoArray");
						   log.warn('%%%%%% cardDocArray: ' + cardDocArray);
						   var planejamentoStateField = servico.instantiate("com.totvs.technology.ecm.dm.ws.CardFieldDto");
						   log.warn("%%%%% planejamentoField: "+planejamentoStateField);
						   planejamentoStateField.setField('processState');
						   planejamentoStateField.setValue("EXECUCAO1");
						   var numProcess = servico.instantiate("com.totvs.technology.ecm.dm.ws.CardFieldDto");
						   numProcess.setField('numProcess');
						   numProcess.setValue(numSolic);
						   cardDocArray.getItem().add(numProcess); 
						   cardDocArray.getItem().add(planejamentoStateField);
						   portServico.updateCardData(empresa, user, password, documentId, cardDocArray);
					   } else {
						   log.warn("%%%%% JÁ FOI ABERTA SOLICITAÇÃO!");			
					   }
				   } else {
					   log.warn("%%%%% NÃO É DO ANO VIGENTE!");			
				   }
			   }

			   log.warn("%%%%% CARD DATA UPDATED!");
			   hAPI.setCardValue("hasOrcamentoThisYear", "Sim");
		   }



	} catch(error) { 
	   log.error('%%%%%% O ERRO ACONTECEU POR: '+error);
	   throw error;
	}
   }

   function getAllPlanejamentos() {

	   var c1 = DatasetFactory.createConstraint("processId", "TRN-002", "TRN-002", ConstraintType.MUST);
	   var dataset = DatasetFactory.getDataset('workflowProcess',null,[c1],['workflowProcessPK.processInstanceId']);
	   return dataset;
   }

function servicetask28(attempt, message) {
 try {
	log.warn('%%%%%% INICIANDO ECMCardService');
	var servico = ServiceManager.getServiceInstance("ECMCardService");
	log.warn('%%%%%% servico: ' + servico);

	var locator = servico.instantiate("com.totvs.technology.ecm.dm.ws.ECMCardServiceService");
	log.warn('%%%%%% locator: ' + locator);

	var portServico = locator.getCardServicePort();
	log.warn('%%%%%% portServico: ' + portServico);

	var user = hAPI.getAdvancedProperty("loginUserWS");
	log.warn('%%%%%% user: ' + user);

	var password = hAPI.getAdvancedProperty("passwdUserWS");
	log.warn('%%%%%% password: ' + password);

	var empresa = parseInt(getValue("WKCompany"));
	log.warn('%%%%%% empresa: ' + empresa + ' TypeOf: ' + typeof empresa);

	// FORM DATA

	var orcamentoCount = parseInt(hAPI.getCardValue("tableOrcamentosCount"));
	for ( var i = 1; orcamentoCount >= i ; i++ ) {
		var numSolic = parseInt(hAPI.getCardValue("numeroSolicitacaoTbOrcamentos___"+ i));
		log.warn('%%%%%% NUMERO DA SOLICITACAO: '+ numSolic);
		var cardDocArray = servico.instantiate("com.totvs.technology.ecm.dm.ws.CardFieldDtoArray");
		log.warn('%%%%%% cardDocArray: ' + cardDocArray);
		var docID = parseInt(hAPI.getCardValue("numeroDocIdTbOrcamentos___"+ i));
		//  update state planejamento
		var orcamentoField = servico.instantiate("com.totvs.technology.ecm.dm.ws.CardFieldDto");
		orcamentoField.setField('orcamento');
		orcamentoField.setValue(hAPI.getCardValue("orcamentoTbOrcamentos___"+ i));
		var processState = servico.instantiate("com.totvs.technology.ecm.dm.ws.CardFieldDto");
		processState.setField('processState');
		processState.setValue("EXECUCAO_OK");
		var numProcess = servico.instantiate("com.totvs.technology.ecm.dm.ws.CardFieldDto");
		numProcess.setField('numProcess');
		numProcess.setValue(numSolic);
		cardDocArray.getItem().add(orcamentoField);
		cardDocArray.getItem().add(processState);
		cardDocArray.getItem().add(numProcess);
		log.warn('%%%%%% cardDocArray: ' + cardDocArray);
		portServico.updateCardData(empresa, user, password, docID, cardDocArray);
		log.warn("%%%%% CARD DATA UPDATED!");
	} 

 } catch(error) { 
	log.error('%%%%%% O ERRO ACONTECEU POR: '+error);
	throw error;
 }
